import { RewardCard } from "./reward-card";

export const AvailableRewardsScrollView = ({rewards}) => {
    return (
        <div className="side-scrolling-card-view">
              {rewards.map((reward, index) => (
                <RewardCard key={index} reward={reward} />
              ))}
        </div>
    );
}
