import React from 'react';
import { BrowserRouter, Route, Routes, Link} from "react-router-dom";
import Home from './components/Home';
import PickupForm from './components/pickup-request/pickup';
import PickupRequests from './components/pickup-request/pickup-history';
import { Rewards } from './components/reward-system/rewards';
import { RewardDetail } from './components/reward-system/reward-detail-page';
import { useState, createContext } from 'react';

//State management: Global context
export const AppContext = createContext();

const App = () => {
  const [rewards, setRewards] = useState([
    {
      id: 1,
      title: 'BOGO on Select Items at MyMart',
      description: 'Buy one, get one free on select items at MyMart.',
      points: 550,
      isRedeemed: false
    },
    {
      id: 2,
      title: '$5 Off Your Next Purchase',
      description: 'Save $5 on your next purchase at MegaStore.',
      points: 50,
      isRedeemed: false
    },
    {
      id: 3,
      title: '20% Off Any Order at DealZone',
      description: 'Get 20% off on your entire order at DealZone.',
      points: 1000,
      isRedeemed: false
    },
    {
      id: 4, 
      title: '$15 Gift Card to FoodWorld',
      description: 'Redeem 150 points for a $15 gift card at FoodWorld.',
      points: 200,
      isRedeemed: false
    },
    {
      id: 5, 
      title: 'Free Movie Ticket at CinePlex',
      description: 'Get a free movie ticket with any purchase at CinePlex.',
      points: 150,
      isRedeemed: false
    },
    {
      id: 6,
      title: '$10 Off Any Item at FashionSquare',
      description: 'Save $10 on any single item at FashionSquare.',
      points: 100,
      isRedeemed: false
    }
  ])

  return (
    <BrowserRouter>
      <Link to={"/rewards"}> Rewards </Link>
      <Routes>
        <Route exact path="/" element={<Home/>} />
        <Route exact path="/pickUp" element={<PickupForm/>} />
        <Route exact path="/pickup-history" element={<PickupRequests/>} />
        <AppContext.Provider value={{rewards, setRewards}}>
          <Route exact path="/rewards" element={<Rewards/>} />
          <Route
            path="/rewards/:rewardId"
            element={<RewardDetail rewards={rewards} />}
          />
        </AppContext.Provider>
        {/* Add more routes here if needed */}
      </Routes>
    </BrowserRouter>
  );
};

export default App;
