import { AvailableRewardsScrollView } from "./available-rewards-scroll-view";
import { BrowserRouter as Router, Route } from 'react-router-dom';
import { RewardDetail } from "./reward-detail-page";


export const Rewards = () => {
    const rewards = [
        {
          title: '20% Off Your Order',
          description: 'Get 20% off on your entire purchase',
          code: 'SAVE20',
          isRedeemed: false
        },
        {
          title: '$10 Off Any Item',
          description: 'Save $10 on any single item in the store',
          code: 'DISCOUNT10',
          isRedeemed: false
        },
        {
          title: '20% Off Your Order',
          description: 'Get 20% off on your entire purchase',
          code: 'SAVE20',
          isRedeemed: false,
        },
        {
          title: '$10 Off Any Item',
          description: 'Save $10 on any single item in the store',
          code: 'DISCOUNT10',
          isRedeemed: false,
        },
        {
          title: '20% Off Your Order',
          description: 'Get 20% off on your entire purchase',
          code: 'SAVE20',
          isRedeemed: false,
        },
        {
          title: '$10 Off Any Item',
          description: 'Save $10 on any single item in the store',
          code: 'DISCOUNT10',
          isRedeemed: false,
        },

      ];

    return (
      <Router>
        {/* Main rewards view */}
        <Route exact path="/rewards">
          <AvailableRewardsScrollView rewards={rewards} />
        </Route>
        {/* Rewards detail page */}
        <Route path="/rewards/:rewardId">
          <RewardDetail rewards={rewards} />
        </Route>
      </Router>
    );
}
