import { AvailableRewardsScrollView } from "./available-rewards-scroll-view";


export const Rewards = () => {
    const rewards = [
      {
        title: 'BOGO on Select Items at MyMart',
        description: 'Buy one, get one free on select items at MyMart.',
        points: 550,
        isRedeemed: false
      },
      {
        title: '$5 Off Your Next Purchase',
        description: 'Save $5 on your next purchase at MegaStore.',
        points: 50,
        isRedeemed: false
      },
      {
        title: '20% Off Any Order at DealZone',
        description: 'Get 20% off on your entire order at DealZone.',
        points: 1000,
        isRedeemed: false
      },
      {
        title: '$15 Gift Card to FoodWorld',
        description: 'Redeem 150 points for a $15 gift card at FoodWorld.',
        points: 200,
        isRedeemed: false
      },
      {
        title: 'Free Movie Ticket at CinePlex',
        description: 'Get a free movie ticket with any purchase at CinePlex.',
        points: 150,
        isRedeemed: false
      },
      {
        title: '$10 Off Any Item at FashionSquare',
        description: 'Save $10 on any single item at FashionSquare.',
        points: 100,
        isRedeemed: false
      }
    ];

    return (
      <div>
          <AvailableRewardsScrollView rewards={rewards} />
      </div>
    );
}
