import { useParams } from 'react-router-dom';

export const RewardDetail = ({ rewards }) => {
  const { rewardId } = useParams();

  // Find the specific reward object based on the rewardId
  const reward = rewards.find((reward) => reward.id === parseInt(rewardId));

  return (
    <div>
      {reward ? (
        <div>
          <h2>{reward.title}</h2>
          <p>{reward.description}</p>
          <p>Is Redeemed: {reward.isRedeemed ? 'Yes' : 'No'}</p>
          <button>Redeem Reward</button>
        </div>
      ) : (
        <p>Reward not found.</p>
      )}
    </div>
  );
};
