import { useContext } from 'react';
import { AppContext } from '../../App.js';

export const Rewards = () => {
  const {rewards} = useContext(AppContext);
    return (
      <div>
          <h3> Available Rewards </h3>
          <AvailableRewardsScrollView rewards={rewards} />
          <h3> Redeemed Rewards </h3>
      </div>
    );
}
